import "./App.css";
import { useEffect, useState, useRef } from "react";

function App() {
  const [isHidden, setIsHidden] = useState(true);
  const dataRef = useRef("");
  useEffect(() => {
    let ignore = false;

    async function getData() {
      try {
        const res = await fetch("http://localhost:8080/");
        const data = await res.json();
        if (!ignore) {
          return data;
          console.log(data);
        }
      } catch (err) {
        console.log(err);
      }
    }

    dataRef.current = getData();

    return () => {
      ignore = true;
    };
  }, []);

  return (
    <>
      <button onClick={() => setIsHidden(!isHidden)}>Click me !</button>
      {isHidden || (
        <div>
          My name is {dataRef.current.name} and my id is {dataRef.current.id}
        </div>
      )}
    </>
  );
}

export default App;
